INSERT INTO `articles` (`id`, `title`, `content`, `created`, `updated`, `author_id`, `category_id`) VALUES (1, 'Associations of fats and carbohydrate intake with cardiovascular disease and mortality in 18 countries from five continents (PURE): a prospective cohort study', 'Is this the end of the low-fat diet? Contrary to popular belief, researchers reviewed the diet of over 100,000 people in 18 countries and discovered that low fat diets may result in a higher risk of premature death.', '2017-08-29 00:00:00', '2019-03-07 23:00:24', 1, 1);
